<?php /** @noinspection ALL */
namespace io\vertx\jphp\ext\sql;


/**
 * Represents the options one can use to customize the unwrapped connection/statement/resultset types
 */
class SQLOptions
{

  public function __construct($sqlOptions = null)
  {

  }

  /**
   * @return bool
   */
  public function isAutoGeneratedKeys()
  {
    return false;
  }

  /**
   * @param $autoGeneratedKeys bool
   * @return $this
   */
  public function setAutoGeneratedKeys($autoGeneratedKeys)
  {
    return $this;
  }

  /**
   * @return array
   */
  public function getAutoGeneratedKeysIndexes()
  {
    return [];
  }

  /**
   * @param $autoGeneratedKeysIndexes array
   * @return $this
   */
  public function setAutoGeneratedKeysIndexes($autoGeneratedKeysIndexes)
  {
    return $this;
  }

  /**
   * @return string
   */
  public function getCatalog()
  {
    return "";
  }

  /**
   * @param $catalog string
   * @return $this
   */
  public function setCatalog($catalog)
  {
    return $this;
  }

  /**
   * @return string
   */
  public function getFetchDirection()
  {
    return null;
  }

  /**
   * @param $fetchDirection string
   * @return $this
   */
  public function setFetchDirection($fetchDirection)
  {
    return $this;
  }

  /**
   * @return integer
   */
  public function getFetchSize()
  {
    return 0;
  }

  /**
   * @param $fetchSize integer
   * @return $this
   */
  public function setFetchSize($fetchSize)
  {
    return $this;
  }

  /**
   * @return integer
   */
  public function getQueryTimeout()
  {
    return 0;
  }

  /**
   * @param $queryTimeout integer
   * @return $this
   */
  public function setQueryTimeout($queryTimeout)
  {
    return $this;
  }

  /**
   * @return bool
   */
  public function isReadOnly()
  {
    return false;
  }

  /**
   * @param $readOnly bool
   * @return $this
   */
  public function setReadOnly($readOnly)
  {
    return $this;
  }

  /**
   * @return string
   */
  public function getResultSetConcurrency()
  {
    return null;
  }

  /**
   * @param $resultSetConcurrency string
   * @return $this
   */
  public function setResultSetConcurrency($resultSetConcurrency)
  {
    return $this;
  }

  /**
   * @return string
   */
  public function getResultSetType()
  {
    return null;
  }

  /**
   * @param $resultSetType string
   * @return $this
   */
  public function setResultSetType($resultSetType)
  {
    return $this;
  }

  /**
   * @return string
   */
  public function getSchema()
  {
    return "";
  }

  /**
   * @param $schema string
   * @return $this
   */
  public function setSchema($schema)
  {
    return $this;
  }

  /**
   * @return string
   */
  public function getTransactionIsolation()
  {
    return null;
  }

  /**
   * @param $transactionIsolation string
   * @return $this
   */
  public function setTransactionIsolation($transactionIsolation)
  {
    return $this;
  }
}